        <script>
            function printPDF(){
                var doc = new jsPDF();
                var titre = document.getElementById('titre').innerHTML;
                var image ='data:image/jpeg;base64,';
                var description = document.getElementById('Description').innerHTML;
                var lesDescription = description.split('<br><br>');
                var contact = document.getElementById('contact').innerHTML;
                var lesContact = contact.split('<br>');
                doc.setFontSize(15);
                doc.text(40,10,titre);
                doc.addImage(image,'JPEG',50,20,100,100);
                doc.text(20,140,lesDescription);
                doc.text(38,200,'Bien trouvé sur:');
                doc.text(38,210,titre);
                doc.text(30,280,lesContact);
                doc.save('Biens.pdf');
            }
        </script>
